import { createStore } from 'vuex'

const store = createStore({
	state() {					// 전역 상태를 반환하는 인스턴스
    	return {				// 여기 전역 상태가 들어갑니다.
        	counter:  123		// 전역 상태의 초기값을 설정합니다.
        }
    }
})

export default store
